@media screen,projection,print{.suez-sitemap{padding-top:10px}:not(.suez-sitemap)+.suez-sitemap{border-top:1px dashed #d9d9d9;margin-top:20px;padding-top:20px}.suez-sitemap:last-of-type{margin-bottom:30px}.suez-sitemap h3{font-size:1em;margin-top:0}.suez-sitemap ul{list-style:none;margin-left:0;margin-right:0 !important;overflow:hidden}.suez-sitemap-compact li{float:left;margin-right:1em;padding-right:1em;position:relative}[dir='rtl'] .suez-sitemap-compact li{float:right;margin-left:1em;margin-right:0;padding-left:1em;padding-right:0}.suez-sitemap-compact li:after{color:#ccc;content:"|";position:absolute;right:0;top:0}[dir='rtl'] .suez-sitemap-compact li:after{left:0;right:auto}.suez-sitemap-compact li:last-child{margin-left:0;margin-right:0}.suez-sitemap-compact li:last-child:after{content:""}.suez-sitemap ul a{color:#15c;display:block}.suez-sitemap-compact ul a{display:inline}}@media screen and (max-width:480px){.suez-sitemap-compact li{float:none;margin:0}.suez-sitemap-compact li:after{content:""}.suez-sitemap-compact ul a{display:block}}