\nClassmessageClass = (NSClassFromString(@"MFMessageComposeViewController"));\r\nNSLog(@"can send SMS [%d]", [messageClass canSendText]);\r\n\r\n    if (messageClass != nil) {\r\n        if ([messageClass canSendText]) {\r\n            [self displaySMS:message];\r\n        } else {\r\n[selfalertWithTitle:nilmsg:@"设备没有短信功能"];\r\n        }\r\n     } else {\r\n[selfalertWithTitle:nilmsg:@"iOS版本过低，iOS4.0以上才支持程序内发送短信"];\r\n     }\r\n}\r\n- (void)messageComposeViewController:(MFMessageComposeViewController *)controller\r\n                                      didFinishWithResult:(MessageComposeResult)result {\r\nNSString*msg;\r\nswitch (result) {\r\ncase MessageComposeResultCancelled: msg = @"发送取消"; break;\r\ncase MessageComposeResultSent: msg = @"发送成功"; [self alertWithTitle:nil msg:msg]; break;\r\ncase MessageComposeResultFailed: msg = @"发送失败"; [self alertWithTitle:nil msg:msg]; break;\r\n    default: break;\r\n}\r\nNSLog(@"发送结果：%@", msg);\r\n[selfdismissModalViewControllerAnimated:YES];\r\n}</pre>', 'iPhone发送消息', '', 'inherit', 'open', 'open', '', '41-autosave', '', '', '2011-08-04 11:23:01', '2011-08-04 03:23:01', '', 41, 'http://localhost/~wsun191/wordpress/?p=43', 0, 'revision', '', 0);
