INSERT INTO `wp_posts` VALUES(79, 1, '2011-08-25 20:00:43', '2011-08-25 12:00:43', '<pre name="code" class="c++">\r\n//system observer\r\nAddObserver : [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(testObserver:) name:@"waydesunobs" object:nil];\r\nPostNotification : [[NSNotificationCenter defaultCenter] postNotificationName:@"waydesunobs" object:nil userInfo:[NSDictionary dictionaryWithObjectsAndKeys:@"haha", @"hoho", nil]];\r\n-(void)testObserver: (NSNotification *)sender\r\n//common: the object is a kind of filter for the notification center filte the subscriber\r\n\r\nObject Observer,\r\n// KVO - key value observer\r\n[objectMessagePublisher addObserver:objectObserver forKeyPath:@"attributeStringName" options:0 context:@"the text for "];\r\n[objectMessagePublisher willChangeValueForKey:@"i"]; i-&gt;attributeStringName\r\n[objectMessagePublisher count:3 secondPar:7]; //objectMessagePublisher changed the attribue''s value\r\n[objectMessagePublisher didChangeValueForKey:@"i"];\r\n//Object observer for value changed\r\nobjectObserver --&gt;  -(void) observeValueForKeyPath:(NSString *)keyPath ofObject:(id)object change:(NSDictionary *)change context:(void *)context\r\nSEL getarr = NSSelectorFromString(keyPath); -- [object performSelector:getarr]\r\n\r\n<strong>Third</strong> part user definition\r\n//Category @interface NSObject ( KLPubSubCategory ) -- So we can use [instance klMsgReceivedWithDataDic:dic]; to call this callback function\r\n</pre>', 'Observer', '', 'publish', 'open', 'open', '', 'observer', '', '', '2012-07-31 18:44:30', '2012-07-31 10:44:30', '', 0, 'http://localhost/~wsun191/wordpress/?p=79', 0, 'post', '', 0);
INSERT INTO `wp_posts` VALUES(80, 1, '2011-08-25 19:44:08', '2011-08-25 11:44:08', '<strong>1: system observer</strong>\n\nAddObserver : [[NSNotificationCenter defaultCenter] addObserver:self selector:@selector(testObserver:) name:@"waydesunobs" object:nil];\n\nPostNotification :    [[NSNotificationCenter defaultCenter] postNotificationName:@"waydesunobs" object:nil userInfo:[NSDictionary dictionaryWithObjectsAndKeys:@"haha", @"hoho", nil]];\n\n-(void)testObserver: (NSNotification *)sender\n\nObject Observer, <strong>KVO</strong> - key value observer\n\n[objectMessagePublisher addObserver:objectObserver forKeyPath:@"attributeStringName" options:0 context:@"the text for "];\n\n[objectMessagePublisher willChangeValueForKey:@"i"];\n\n[objectMessagePublisher count:3 secondPar:7]; //objectMessagePublisher changed the attribue''s value\n\n[objectMessagePublisher didChangeValueForKey:@"i"];', 'Observer', '', 'inherit', 'open', 'open', '', '79-revision', '', '', '2011-08-25 19:44:08', '2011-08-25 11:44:08', '', 79, 'http://localhost/~wsun191/wordpress/?p=80', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(81, 1, '2011-08-25 20:08:30', '2011-08-25 12:08:30', '<pre name="code" class="c++">Selector : selector for method\r\n[self selectorTest:@"selector2"]; equals to the next solution\r\n[self performSelector:@selector(selectorTest:) withObject:@"selector2"];\r\n\r\nSelector : selector for attribute\r\nSEL getarr = NSSelectorFromString(keyPath);\r\n[object performSelector:getarr];</pre>', 'Selector', '', 'publish', 'open', 'open', '', 'selector', '', '', '2012-07-31 18:40:28', '2012-07-31 10:40:28', '', 0, 'http://localhost/~wsun191/wordpress/?p=81', 0, 'post', '', 1);
