INSERT INTO `wp_posts` VALUES(83, 1, '2011-08-25 20:14:07', '2011-08-25 12:14:07', '<pre name="code" class="c++">\r\nNSArray *myNSArray = [NSArray arrayWithObjects:[NSArray array], @"stirng", @"string1", nil];\r\n/* Predeclare a block */\r\nBOOL (^IsSameClass)(id, NSUInteger, BOOL *) = ^(id element, NSUInteger idx, BOOL * stop) {\r\n  *stop = [element isKindOfClass: [NSString class]];\r\n  return (*stop);\r\n};	\r\nNSUInteger index = [myNSArray indexOfObjectPassingTest:IsSameClass];\r\n/* Using a block literal */\r\nNSUInteger jndex = [myNSArray indexOfObjectPassingTest:^(id element, NSUInteger idx, BOOL * stop){ \r\n   *stop = [element isKindOfClass:[NSString class]]; \r\n   return (*stop);\r\n}];</pre>', 'NSArray', '', 'publish', 'open', 'open', '', 'nsarray', '', '', '2012-07-31 18:39:45', '2012-07-31 10:39:45', '', 0, 'http://localhost/~wsun191/wordpress/?p=83', 0, 'post', '', 0);
