INSERT INTO `wp_posts` VALUES(136, 1, '2012-07-27 23:48:47', '2012-07-27 15:48:47', '<pre name="code" class="c++">\n/* Points. */\nstruct CGPoint {\nCGFloat x;\nCGFloat y;\n};\n\ntypedef struct CGPoint CGPoint;\n\n/* Sizes. */\nstruct CGSize {\nCGFloat width;\nCGFloat height;\n};\n\ntypedef struct CGSize CGSize;\n/* Rectangles. */\nstruct CGRect {\nCGPoint origin;\nCGSize size;\n};\n\ntypedef struct CGRect CGRect;\n\nCG_INLINE CGRect\n\nCGRectMake(CGFloat x, CGFloat y, CGFloat width, CGFloat height)\n{\nCGRect rect;\nrect.origin.x = x; rect.origin.y = y;\nrect.size.width = width; rect.size.height = height;\nreturn rect;\n}\n\n</pre>', 'ios struct', '', 'inherit', 'open', 'open', '', '68-autosave', '', '', '2012-07-27 23:48:47', '2012-07-27 15:48:47', '', 68, 'http://www.ihakula.com/uncategorized/68-autosave/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(137, 1, '2011-08-20 11:26:32', '2011-08-20 03:26:32', '/* Points. */\r\n\r\nstruct CGPoint {\r\n\r\nCGFloat x;\r\n\r\nCGFloat y;\r\n\r\n};\r\n\r\ntypedef struct CGPoint CGPoint;\r\n\r\n&nbsp;\r\n\r\n/* Sizes. */\r\n\r\nstruct CGSize {\r\n\r\nCGFloat width;\r\n\r\nCGFloat height;\r\n\r\n};\r\n\r\ntypedef struct CGSize CGSize;\r\n\r\n&nbsp;\r\n\r\n/* Rectangles. */\r\n\r\n&nbsp;\r\n\r\nstruct CGRect {\r\n\r\nCGPoint origin;\r\n\r\nCGSize size;\r\n\r\n};\r\n\r\ntypedef struct CGRect CGRect;\r\n\r\n&nbsp;\r\n\r\nCG_INLINE CGRect\r\n\r\nCGRectMake(CGFloat x, CGFloat y, CGFloat width, CGFloat height)\r\n\r\n{\r\n\r\nCGRect rect;\r\n\r\nrect.origin.x = x; rect.origin.y = y;\r\n\r\nrect.size.width = width; rect.size.height = height;\r\n\r\nreturn rect;\r\n\r\n}\r\n\r\n&nbsp;', 'ios struct', '', 'inherit', 'open', 'open', '', '68-revision-3', '', '', '2011-08-20 11:26:32', '2011-08-20 03:26:32', '', 68, 'http://www.ihakula.com/uncategorized/68-revision-3/', 0, 'revision', '', 0);
INSERT INTO `wp_posts` VALUES(140, 1, '2012-07-28 00:13:09', '2012-07-27 16:13:09', '<a href="http://www.ihakula.com/wp-content/uploads/2012/07/photo.jpg"><img class="alignleft size-full wp-image-141" title="刷卡进站外壳" src="http://www.ihakula.com/wp-content/uploads/2012/07/photo.jpg" alt="" width="640" height="480" /></a>\r\n\r\n传说中可以在iphone上戴上这么个玩意儿就可以刷卡上公交，进地铁了，今天刚把就把公交卡塞进了这个壳里面，然后从iphone后面把它给罩上。\r\n到了五道口地铁站，有点小激动的掏出iphone，心想让别人见识一下刷iphone进站（目前还没有什么ios应用可以满足这个需求），心想也得瑟一回，zhuang个bility，结果刷了好几下没刷上...... 后面又还有那么多人，心想不能当着别人面把壳给抠下来，再拿出卡来刷啊，硬着头皮就进去了，居然没有拦我（这个滥刷卡机）。\r\n最后到了站，微笑着冲着大姐喊到，刚进站没刷上，帮我从外现刷一下，谢谢。\r\n\r\n...... 我不会告诉你，在这之前，我在裤兜里把卡从这个传说中的壳中拿出来的......', '传说中的刷iphone上公交，进地铁', '', 'publish', 'open', 'open', '', '%e4%bc%a0%e8%af%b4%e4%b8%ad%e7%9a%84%e5%88%b7iphone%e4%b8%8a%e5%85%ac%e4%ba%a4%ef%bc%8c%e8%bf%9b%e5%9c%b0%e9%93%81', '', '', '2012-07-28 00:15:32', '2012-07-27 16:15:32', '', 0, 'http://www.ihakula.com/?p=140', 0, 'post', '', 1);
