INSERT INTO `wp_posts` VALUES(152, 4, '2012-07-30 17:02:07', '2012-07-30 09:02:07', '都市爱情爆笑喜剧《爱情公寓3》（爱情公寓第三季）由上影集团、高格文化、辛加迪影视联合出品，导演韦正执导，汪远编剧，娄艺潇、陈赫、李金铭、赵霁、王传君、孙艺洲、邓家佳、金世佳、李佳航等人主演。该剧将于2012年7月30日晚19点30分登陆安徽卫视、陕西卫视、湖北卫视三大卫视联合上星首播，东方卫视31日白天联播，优酷网络上独播。', '爱情公寓3', '', 'publish', 'open', 'open', '', '%e7%88%b1%e6%83%85%e5%85%ac%e5%af%933', '', '', '2012-07-30 17:02:07', '2012-07-30 09:02:07', '', 0, 'http://www.ihakula.com/?p=152', 0, 'post', '', 1);
