INSERT INTO `wp_posts` VALUES(190, 1, '2011-08-25 20:14:07', '2011-08-25 12:14:07', '<pre>NSArray *myNSArray = [NSArray arrayWithObjects:[NSArray array], @"stirng", @"string1", nil];\r\n/* Predeclare a block */\r\nBOOL (^IsSameClass)(id, NSUInteger, BOOL *) = ^(id element, NSUInteger idx, BOOL * stop) {\r\n  *stop = [element isKindOfClass: [NSString class]];\r\n  return (*stop);\r\n};	\r\nNSUInteger index = [myNSArray indexOfObjectPassingTest:IsSameClass];\r\n/* Using a block literal */\r\nNSUInteger jndex = [myNSArray indexOfObjectPassingTest:^(id element, NSUInteger idx, BOOL * stop){ \r\n   *stop = [element isKindOfClass:[NSString class]]; \r\n   return (*stop);\r\n}];</pre>', 'NSArray', '', 'inherit', 'open', 'open', '', '83-revision-2', '', '', '2011-08-25 20:14:07', '2011-08-25 12:14:07', '', 83, 'http://www.ihakula.com/uncategorized/83-revision-2/', 0, 'revision', '', 0);
